SELECT w_warehouse_name, i_item_id, inv_before, inv_after FROM (SELECT w.w_warehouse_name, i.i_item_id, SUM(CASE WHEN (d.d_date < '2000-05-19') THEN inv.inv_quantity_on_hand ELSE 0 END) AS inv_before, SUM(CASE WHEN (d.d_date >= '2000-05-19') THEN inv.inv_quantity_on_hand ELSE 0 END) AS inv_after FROM inventory AS inv JOIN warehouse AS w ON inv.inv_warehouse_sk = w.w_warehouse_sk JOIN item AS i ON inv.inv_item_sk = i.i_item_sk JOIN date_dim AS d ON inv.inv_date_sk = d.d_date_sk WHERE i.i_current_price BETWEEN 0.99 AND 1.49 AND d.d_date BETWEEN date_sub('2000-05-19', interval 30 day) AND date_add('2000-05-19', interval 30 day) GROUP BY w.w_warehouse_name, i.i_item_id) AS x WHERE CASE WHEN inv_before > 0 THEN inv_after / inv_before ELSE NULL END BETWEEN 0.6666666666666666 AND 1.5 ORDER BY w_warehouse_name, i_item_id LIMIT 100
