SELECT i_item_id, avg(cs_quantity), avg(cs_list_price), avg(cs_coupon_amt), avg(cs_sales_price) FROM catalog_sales JOIN date_dim ON catalog_sales.cs_sold_date_sk = date_dim.d_date_sk JOIN item ON catalog_sales.cs_item_sk = item.i_item_sk JOIN customer_demographics ON catalog_sales.cs_bill_cdemo_sk = customer_demographics.cd_demo_sk JOIN promotion ON catalog_sales.cs_promo_sk = promotion.p_promo_sk WHERE customer_demographics.cd_gender = 'F' AND customer_demographics.cd_marital_status = 'M' AND customer_demographics.cd_education_status = '4 yr Degree' AND (promotion.p_channel_email = 'N' OR promotion.p_channel_event = 'N') AND date_dim.d_year = 2000 GROUP BY i_item_id ORDER BY i_item_id LIMIT 100
